import React, { useState, useEffect } from "react";
import Filter from "./components/filter";
import Form from "./components/form";
import List from "./components/list";

import axios from "axios";

const App = () => {
  const [countries, setCountries] = useState([]);
  const [filterWord, setFilterWord] = useState("");

  //Initial Data
  useEffect(() => {
    //fetch to localhost:3001
    axios
      .get("https://restcountries.eu/rest/v2/all")
      //setCountries con data
      .then((response) => setCountries(response.data));
  }, []);

  const handleClick = (newCountry) => {
    const findit = countries.find(
      (country) => country.name.toLowerCase() === newCountry.name.toLowerCase()
    );
    if (findit) alert(`${newCountry.name} is already added to phonebook`);
    else setCountries(countries.concat(newCountry));
  };

  const handleSearch = (word) => {
    setFilterWord(word);
  };
  const filteredList = countries.filter((pers) =>
    pers.name.toLowerCase().includes(filterWord.toLowerCase())
  );

  return (
    <>
      <h2>Phonebook</h2>
      <Filter handleSearch={handleSearch} />
      <h2>Add new One</h2>
      <Form handleClick={handleClick} />
      <h2>Numbers</h2>
      <List countries={filteredList} />
    </>
  );
};

export default App;
