    ev.preventDefault;
    console.log(ev.target.value);
    setNewName = ev.targe.value;
  };
  return (
    <>
      <div>
        <h2>Phonebook</h2>
        <form>
          <div>
            name: <input />
          </div>
          <div>
            <button onClick={handleClick} type="submit">
              add
            </button>
          </div>
        </form>
