import React, { useState } from "react";
import Filter from "./components/filter";
import Form from "./components/form";
import List from "./components/list";

const App = () => {
  // const [persons, setPersons] = useState([
  //   { name: "Arto Hellas", phone: "123-456-789" },
  // ]);
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" },
  ]);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [filterWord, setFilterWord] = useState("");

  const handleClick = (ev) => {
    ev.preventDefault();
    const findit = persons.find(
      (pers) => pers.name.toLowerCase() === newName.toLowerCase()
    );
    if (findit) alert(`${newName} is already added to phonebook`);
    else setPersons(persons.concat({ name: newName, phone: newPhone }));

    setNewName("");
  };
  const handleNameChange = (ev) => {
    setNewName(ev.target.value);
  };
  const handlePhoneChange = (ev) => {
    setNewPhone(ev.target.value);
  };

  const handleSearch = (word) => {
    setFilterWord(word);
  };
  return (
    <>
      <Filter handleSearch={handleSearch} />
      <h2>Add new One</h2>
      <form onSubmit={handleClick}>
        <div>
          name:{" "}
          <input
            onChange={handleNameChange}
            type="text"
            name="name"
            value={newName}
          />
        </div>
        <div>
          number:{" "}
          <input
            onChange={handlePhoneChange}
            type="number"
            name="phone"
            value={newPhone}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons
        .filter((pers) =>
          pers.name.toLowerCase().includes(filterWord.toLowerCase())
        )
        .map((pers) => (
          <p key={pers.name}>
            <span>{pers.name}</span>
            {": "}
            {pers.phone}
          </p>
        ))}
    </>
  );
};

export default App;
