import React, { useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([{ name: "Arto Hellas" }]);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");

  const handleClick = (ev) => {
    ev.preventDefault();
    // const promise = axios.get(
    //   "http://15.237.18.108:8020/damcloudtaservice/rest/meta/allasset/lang/cat"
    // );
    // promise.then((resp) => {
    //   console.log(resp.data);
    // });
    const findit = persons.find(
      (pers) => pers.name.toLowerCase() === newName.toLowerCase()
    );
    if (findit) alert(`${newName} is already added to phonebook`);
    else setPersons(persons.concat({ name: newName }));
    setNewName("");
  };
  const handleNameChange = (ev) => {
    setNewName(ev.target.value);
  };
  const handlePhoneChange = (ev) => {
    setNewPhone(ev.target.value);
  };
  return (
    <>
      <div>
        <h2>Phonebook</h2>
        <form onSubmit={handleClick}>
          <div>
            name:{" "}
            <input
              onChange={handleNameChange}
              type="text"
              name="input"
              value={newName}
            />
          </div>
          <div>
            number:{" "}
            <input
              onChange={handlePhoneChange}
              type="text"
              name="input"
              value={newPhone}
            />
          </div>
          <div>
            <button type="submit">add</button>
          </div>
        </form>
        <h2>Numbers</h2>
        {persons.map((pers) => (
          <p key={pers.name}>{pers.name}</p>
        ))}
      </div>
    </>
  );
};

export default App;
