import React, { useState } from "react";

const App = () => {
  // const [persons, setPersons] = useState([
  //   { name: "Arto Hellas", phone: "123-456-789" },
  // ]);
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" },
  ]);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const filterWord = "";

  const handleClick = (ev) => {
    ev.preventDefault();
    // const promise = axios.get(
    //   "http://15.237.18.108:8020/damcloudtaservice/rest/meta/allasset/lang/cat"
    // );
    // promise.then((resp) => {
    //   console.log(resp.data);
    // });
    const findit = persons.find(
      (pers) => pers.name.toLowerCase() === newName.toLowerCase()
    );
    if (findit) alert(`${newName} is already added to phonebook`);
    else setPersons(persons.concat({ name: newName, phone: newPhone }));

    setNewName("");
  };
  const handleNameChange = (ev) => {
    setNewName(ev.target.value);
  };
  const handlePhoneChange = (ev) => {
    setNewPhone(ev.target.value);
  };

  const handleSearch = (ev) => {
    console.log(ev.target.value);
  };
  return (
    <>
      <div>
        <h2>Phonebook</h2>
        <div>
          name:{" "}
          <input
            onChange={handleSearch}
            type="text"
            name="input"
            value={newName}
          />
        </div>

        <h2>Add new One</h2>
        <form onSubmit={handleClick}>
          <div>
            name:{" "}
            <input
              onChange={handleNameChange}
              type="text"
              name="input"
              value={newName}
            />
          </div>
          <div>
            number:{" "}
            <input
              onChange={handlePhoneChange}
              type="text"
              name="input"
              value={newPhone}
            />
          </div>
          <div>
            <button type="submit">add</button>
          </div>
        </form>
        <h2>Numbers</h2>
        {persons
          .filter((pers) =>
            pers.name.toLowerCase().includes(filterWord.toLowerCase())
          )
          .map((pers) => (
            <p key={pers.name}>
              <span>{pers.name}</span>
              {": "}
              {pers.phone}
            </p>
          ))}
      </div>
    </>
  );
};

export default App;
